def get_db_data(path="/home/maximl/Data/Experiment_data/newcastle/C7_experiment-800/db.sqlite3"):
    conn = sqlite3.connect(path)
    c = conn.cursor()
    rows = c.execute("SELECT * FROM singlecell_annotation").fetchall()
    return pandas.DataFrame.from_records(rows, exclude="0", columns=["0", "id", "dataset", "label"])